const apiUrl = 'https://tarotbotbackend-production.up.railway.app/reading';

export const getTarotReading = async (message, cardOne, cardTwo, cardThree) => {
  try {
    const userQuery = document.querySelector('#query').value;

    const requestOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        model: "text-davinci-003",
        prompt: message,
        temperature: 0.7,
        max_tokens: 2000,
      }),
    };

    const response = await fetch(apiUrl, requestOptions);
    const data = await response.json();

    console.log(data.choices[0].text);

    return responseBox.innerHTML = data.choices[0].text;
  } catch (error) {
    console.error('Error while getting the tarot reading:', error);
    throw new Error('Failed to get tarot reading.');
  }
};
